'use client';

import { useState, useEffect, useContext, use } from 'react';
import { useRouter } from 'next/navigation';
import api from '../../../../../lib/api';
import AuthContext from '../../../../../features/auth/context/AuthContext';
import { toast } from 'react-toastify';
import { FaArrowLeft, FaSave } from 'react-icons/fa';

const ProductEditPage = ({ params }: { params: Promise<{ id: string }> }) => {
    // Unwrap params for Next.js 15+ (async params)
    const { id } = use(params);
    const { user, loading: authLoading } = useContext(AuthContext) as any;
    const router = useRouter();

    const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [image, setImage] = useState('');
    const [brand, setBrand] = useState('');
    const [category, setCategory] = useState('');
    const [countInStock, setCountInStock] = useState(0);
    const [description, setDescription] = useState('');
    const [uploading, setUploading] = useState(false);

    // Auth Protection
    useEffect(() => {
        if (!authLoading) {
            if (!user || !user.isAdmin) {
                router.push('/login');
            }
        }
    }, [user, authLoading, router]);

    useEffect(() => {
        const fetchProduct = async () => {
            // In Next.js, this page is only reached if there is an ID (edit route)
            try {
                const { data } = await api.get(`/api/products/${id}`);
                setName(data.name);
                setPrice(data.price);
                setImage(data.image);
                setBrand(data.brand);
                setCategory(data.category);
                setCountInStock(data.countInStock);
                setDescription(data.description);
            } catch {
                toast.error('Failed to fetch data');
            }
        };

        if (user && user.isAdmin) {
            fetchProduct();
        }
    }, [id, user]);

    const uploadFileHandler = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;

        const formData = new FormData();
        formData.append('image', file);
        setUploading(true);

        try {
            const config = {
                headers: {
                    'Content-Type': 'multipart/form-data',
