'use client';

import { createContext, useState, useEffect, ReactNode } from 'react';
import { toast } from 'react-toastify';
import { CartItem, ShippingAddress, CartContextType } from '../types';

const CartContext = createContext<CartContextType | undefined>(undefined);

interface CartProviderProps {
    children: ReactNode;
}

export const CartProvider = ({ children }: CartProviderProps) => {
    const [cartItems, setCartItems] = useState<CartItem[]>([]);
    const [shippingAddress, setShippingAddress] = useState<ShippingAddress>({});
    const [paymentMethod, setPaymentMethod] = useState<string>('Razorpay');
    const [isInitialized, setIsInitialized] = useState(false);

    // Initialize from LocalStorage
    useEffect(() => {
        const storedCart = localStorage.getItem('cartItems');
        if (storedCart) {
            setCartItems(JSON.parse(storedCart));
        }

        const storedAddress = localStorage.getItem('shippingAddress');
        if (storedAddress) {
            setShippingAddress(JSON.parse(storedAddress));
        }

        const storedPayment = localStorage.getItem('paymentMethod');
        if (storedPayment) {
            setPaymentMethod(JSON.parse(storedPayment));
        }
        setIsInitialized(true);
    }, []);

    // Sync updates to LocalStorage
    useEffect(() => {
        if (isInitialized) {
            localStorage.setItem('cartItems', JSON.stringify(cartItems));
        }
    }, [cartItems, isInitialized]);

    useEffect(() => {
        if (isInitialized) {
            localStorage.setItem('shippingAddress', JSON.stringify(shippingAddress));
        }
    }, [shippingAddress, isInitialized]);

    useEffect(() => {
        if (isInitialized) {
            localStorage.setItem('paymentMethod', JSON.stringify(paymentMethod));
        }
    }, [paymentMethod, isInitialized]);

    const addToCart = (product: any, qty: number) => {
        const existItem = cartItems.find((x) => x.product === product._id);
        const currentQty = existItem ? existItem.qty : 0;
        const finalQty = currentQty + qty;

        if (finalQty > product.countInStock) {
            toast.error(`Stock limit reached. Max available: ${product.countInStock}`);
            return;
        }

        const item: CartItem = {
            product: product._id,
            name: product.name,
            image: product.image,
            price: product.price,
            countInStock: product.countInStock,
            qty: finalQty,
            size: product.size // Ensure size is preserved if passed in product object
        };

        let newCartItems;
        if (existItem) {
            newCartItems = cartItems.map((x) =>
                x.product === existItem.product ? item : x
            );
        } else {
            newCartItems = [...cartItems, item];
        }

        setCartItems(newCartItems);
    };

    const removeFromCart = (id: string) => {
        const newCartItems = cartItems.filter((x) => x.product !== id);
        setCartItems(newCartItems);
    };

    const saveShippingAddress = (data: ShippingAddress) => {
        setShippingAddress(data);
    };

    const savePaymentMethod = (data: string) => {
        setPaymentMethod(data);
    };

    const clearCart = () => {
        setCartItems([]);
        localStorage.removeItem('cartItems');
    };

    return (
        <CartContext.Provider value={{
            cartItems,
            addToCart,
            removeFromCart,
            clearCart,
            shippingAddress,
            saveShippingAddress,
            paymentMethod,
            savePaymentMethod
        }}>
            {children}
        </CartContext.Provider>
    );
};

export default CartContext;

export interface CartItem {
    product: string;
    name: string;
    image: string;
    price: number;
    countInStock: number;
    qty: number;
    size?: string;
}

export interface ShippingAddress {
    address?: string;
    city?: string;
    postalCode?: string;
    country?: string;
}

export interface CartContextType {
    cartItems: CartItem[];
    addToCart: (product: any, qty: number) => void;
    removeFromCart: (id: string) => void;
    clearCart: () => void;
    shippingAddress: ShippingAddress;
    saveShippingAddress: (data: ShippingAddress) => void;
    paymentMethod: string;
    savePaymentMethod: (data: string) => void;
}

