'use client';

import Link from 'next/link';
import { motion } from 'framer-motion';
import { FaBookmark, FaRegBookmark } from 'react-icons/fa';
import { useContext } from 'react';
import WishlistContext, { WishlistContextType } from '../../wishlist/context/WishlistContext';
import { Product } from '../types';

interface ProductCardProps {
    product: Product;
    onQuickView?: (product: Product) => void;
}

const ProductCard = ({ product, onQuickView }: ProductCardProps) => {
    const { addToWishlist, removeFromWishlist, isInWishlist } = useContext(WishlistContext) as WishlistContextType;
    const inWishlist = isInWishlist(product._id);

    const toggleWishlist = (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();
        if (inWishlist) {
            removeFromWishlist(product._id);
        } else {
            addToWishlist(product);
        }
    };

    return (
        <div className="group relative flex flex-col gap-4 cursor-pointer">
